function ListElements(props) {
    const characters = props.characters;
	console.log(characters);
  	const listItems = characters.map((character, index) =>
  		<li key={index}>
  	      character.index
        </li>
  	);
  	return (
		<ul>{listItems.index} </ul>
  	);
}

class InformationBox extends Component {
    constructor() {
        super()
        this.state = {
            loading: true,
            character: []
        }
    }

    componentDidMount() {
        this.setState({loading: true})
        fetch("https://swapi.co/api/people/")
            .then(response => response.json())
            .then(data => {
                this.setState({
                    loading: false,
                    character: data
                })
            })
    }

    render() {
        const text = this.state.loading ? "loading..." : this.state.character.results[0].name
        if(this.state.loading === true){
        	console.log("entra aqui")
        	return(
				<p>{text}</p>
        	)
		}
		else{
			return(
				<ListElements characters={this.state.character.results} />
			)
		}
    }
}

export default InformationBox
