import React, {Component} from "react"

// https://developer.mozilla.org/en-US/docs/Web/API/WindowOrWorkerGlobalScope/fetch
// https://swapi.co/
// https://medium.com/javascript-scene/master-the-javascript-interview-what-is-a-promise-27fc71e77261

function ListElements(props) {
    const characters = props.results;
	console.log(props);
  	const listItems = props.map((theCharacter) =>
		<li>{theCharacter}</li>
  	);
  	return (
		<ul>{listItems}</ul>
  	);
}

class App extends Component {
    constructor() {
        super()
        this.state = {
            loading: true,
            character: []
        }
    }

    componentDidMount() {
        this.setState({loading: true})
        fetch("https://swapi.co/api/people/")
            .then(response => response.json())
            .then(data => {
                this.setState({
                    loading: false,
                    character: data
                })
            })
    }

    render() {
        const text = this.state.loading ? "loading..." : this.state.character.results[0].name
        if(this.state.loading === true){
        	return(
				<p>{text}</p>
        	)
		}
		else{
			return(
				<ListElements characters={this.state.character.results} />
				//<p>{this.state.character.results[0].name}</p>
			)
		}
    }
}

export default App
